import config from "./_apiConfig/apiConfig";
import { performRequest, apiEndPoints, methodType } from "./_apiConfig";

export const getCustomers = (data = {}) => {
  return performRequest(
    methodType.GET,
    config.baseURL + apiEndPoints.customer + 'GetList',
    data,
    true,
    false
  )
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
}

export const createCustomers = (data = {}) => {
  return performRequest(methodType.POST, apiEndPoints.addCustomer + 'Insert', data)
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
};

export const deleteDepartment = (id) => {
  return performRequest(methodType.DELETE, apiEndPoints.coreDepartment + id + "/")
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
};

export const updateDepartment = (data = {}) => {
  return performRequest(
    methodType.PUT,
    apiEndPoints.coreDepartment + data?._id + "/",
    data,
    true
  )
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
};
