import React, { useState } from 'react'
import { InputField } from '../common/InputField'

const formObj = {
    userName: '',
    password: '',
}

export default function Login() {

    const [formValue, setFormValue] = useState(formObj);
    const [formError, setFormError] = useState("");
    const [loading, setLoading] = useState(false);

    const handleOnChange = async (name, value) => {
        const stateObj = { ...formValue, [name]: value };
        setFormValue(stateObj);
        setResponseError('');
        if (!utils.isObjectKeyEmpty(formError)) {
            const validationResult = await utils.checkFormError(stateObj, errorSchema.jobPostingSchema);
            if (validationResult === true) {
                setFormError("");
            } else {
                setFormError(validationResult);
            }
        }
    };

    const handleSubmitClick = async (e) => {
        e.preventDefault();

        const validationResult = await utils?.checkFormError(formValue, errorSchema.jobPostingSchema);

        if (utils?.isObjectKeyEmpty(validationResult)) {
            setLoading(true);
            try {
                let payload = {
                    ...formValue,
                    skills: formValue?.skills?.map((e, i) => e.value),
                    language: formValue?.language?.map((e, i) => e.value),
                    experience_level: +formValue?.experience_level,
                    company_country: formValue?.company_country?.value,
                    company_state: formValue?.company_state?.label,
                }
                if (id) {
                    dispatch(action.editJobPost(payload, formCallBackFunction))
                } else {
                    dispatch(action.createJobPost(payload, handleLoading))
                }

                // const res = await callStep1(formValue);
                // if (!!res?.success) {
                //     toast.success(`${res?.message}`);
                //     setFormValue('');
                //     setLoading(false);
                // } else {
                //     if (res?.error?.business_name) {
                //         setResponseError(res?.error?.business_name[0])
                //     }
                //     setLoading(false);
                // }
            } catch (error) {
                console.log("errr", error);
            }
            setFormError("");
        } else {
            setFormError(validationResult);
            setLoading(false);
        }
    };

    return (
        <>
            <div className="container">
                <div className="row m-5 no-gutters shadow-lg">
                    <div className="col-md-6 d-none d-md-block img_ajn p-5">
                        <img
                            src="https://images.unsplash.com/photo-1566888596782-c7f41cc184c5?ixlib=rb-1.2.1&auto=format&fit=crop&w=2134&q=80"
                            className="img-fluid"
                            style={{ minHeight: "100%" }}
                        />
                    </div>
                    <div className="col-md-6 bg-white p-5">
                        <h3 className="pb-3">Login</h3>
                        <div className="form-style mt-5">
                            <form>
                                <div className="form-group pb-3">
                                    <InputField
                                        placeholder="User Name"
                                        name='userName'
                                        type='text'
                                        value={formValue?.userName}
                                        focus={!!(typeof formError === "object" && formError?.userName)}
                                        error={!!(typeof formError === "object") ? formError?.userName : ""}
                                        onChange={({ target: { name, value } }) =>
                                            handleOnChange(name, value)
                                        }
                                    />
                                    {/* <input
                                        type="email"
                                        placeholder="Email"
                                        className="form-control"
                                        id="exampleInputEmail1"
                                        aria-describedby="emailHelp"
                                    /> */}
                                </div>
                                <div className="form-group pb-3">
                                    <input
                                        type="password"
                                        placeholder="Password"
                                        className="form-control"
                                        id="exampleInputPassword1"
                                    />
                                </div>

                                <div className="pb-2">
                                    <button
                                        type="submit"
                                        className="btn btn-primary w-100 font-weight-bold mt-2"
                                    >
                                        Submit
                                    </button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
