import React, { useEffect, useState } from 'react'
import CommonButton from '../../common/CommonButton'
import { getBillList } from '../../../services/billServies';

export default function Bill() {

  const [billList, setBillList] = useState([])

  const getBillData = async () => {
    const response = await getBillList();
    setBillList(response);
  }

  useEffect(() => {
    getBillData()
  }, [])

  return (
    <div className='container'>
      <div className="common_flex p-3">
        <p className='bill_heading'> Bill </p>
        <div className='flex-gap'>
          <CommonButton name="Add" />
          <CommonButton name="Print" />
          <CommonButton name="Close" />
        </div>
      </div>

      <table class="table table-hover table-bordered">
        <thead>
          <tr>
            <th scope="col">Sr. No.</th>
            <th scope="col">Bill No</th>
            <th scope="col">Date</th>
            <th scope="col">Customer Name</th>
            <th scope="col">Net Amount</th>
            <th scope="col">Remarks</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        {
          billList?.length > 0 ? (
            billList?.map((el, ind) => {
              return (
                <tbody>
                  <tr>
                    <th scope="row">{ind + 1}</th>
                    <td>{el?.billNo}</td>
                    <td>{new Date(el?.billDate).toDateString()}</td>
                    <td>{el?.customerName}</td>
                    <td>{(el?.netAmount).toFixed(2)}</td>
                    <td>{el?.remarks ? el?.remarks : '-'}</td>
                    <td>
                      <span className='questions_create'> <i class='bx bx-edit-alt'></i> </span> &nbsp;
                      <span className='questions_delete'> <i class='bx bxs-trash'></i> </span>
                    </td>
                  </tr>
                </tbody>
              )
            })
          ) : (
            <> No data found </>
          )
        }
      </table>

      <InputModal
        isOpen={Boolean(isOpen)}
        headerName={isEdit ? "Edit Attendance" : "Add Attendance"}
        toggle={() => closeModal()}
        submitLabel={isEdit ? "Update Attendance" : "Add Attendance"}
        onSubmit={(e) => onSubmit(e)}
        disabled={isEdit ? JSON.stringify(formValue) === JSON.stringify(disableInp) : ""}
        loading={loading}
        inputProps={
          <>
            <div className="row">
              <div className="col-md-12">
                <label className="modal_label mb-2"> {<span> Employee <span className="redStar_color"> * </span></span>}</label>
                <Dropdown
                  placeholder="Select Employee"
                  className="anvSel_drop"
                  name="emp_id"
                  options={
                    employeeList?.length > 0
                      ? employeeList?.map((e) => {
                        return { value: e.employee_id, label: e.fullname };
                      })
                      : []
                  }
                  selectedOption={formValue?.emp_id}
                  setSelectedOption={(data) => {
                    handleOnChange("emp_id", data?.value);
                    setFormValue((s) => ({ ...s, emp_name: data?.label }));
                  }}
                  valueText="value"
                  labelText="label"
                  focus={!!formError.emp_id}
                  error={formError.emp_id}
                />
              </div>
              <div className="col-md-12">
                <label className="modal_label mb-2">
                  Shift Type <span className="redStar_color"> * </span>
                </label>
                <Dropdown
                  className="anvSel_drop"
                  placeholder="Select Shift Type"
                  name="shift_type"
                  options={shiftTypeList ? shiftTypeList : []}
                  setSelectedOption={(data) => {
                    handleOnChange("shift_type", data.value);
                  }}
                  valueText="_id"
                  labelText="label"
                  selectedOption={formValue?.shift_type}
                  focus={!!formError.shift_type}
                  error={formError.shift_type}
                />
              </div>
              <div className="col-md-6 mt-3">
                <div className="anvDates">
                  <label className="modal_label mb-2"> {<span> Date <span className="redStar_color"> * </span></span>}</label>
                  <InputField
                    type="date"
                    className="modal_input"
                    placeholder="Date"
                    name="date"
                    focus={!!formError.date}
                    error={formError.date}
                    value={formValue.date}
                    min={formattedLastTwoDays}
                    max={formattedCurrentDate}
                    onChange={({ target: { name, value } }) =>
                      handleOnChange(name, value)
                    }
                  />
                  <i class="bx bx-calendar"></i>
                </div>
              </div>

              <div className="col-md-6 mt-3">
                <label className="modal_label mb-2"> {<span> Status <span className="redStar_color"> * </span></span>}</label>
                <Dropdown
                  placeholder="Select Status"
                  className="anvSel_drop"
                  name="status"
                  options={
                    statusArray?.length > 0
                      ? statusArray?.map((e) => {
                        return { value: e.value, label: e.label };
                      })
                      : []
                  }
                  selectedOption={formValue?.status}
                  setSelectedOption={(data) => {
                    handleOnChange("status", data?.value);
                  }}
                  valueText="value"
                  labelText="label"
                  focus={!!formError.status}
                  error={formError.status}
                />
              </div>

              {
                formValue?.status === 'Present' && (
                  <div className="col-md-12">
                    <div className="row">
                      <div className="col-md-6">
                        <div className="anvDates">
