import * as Yup from "yup";

export const errorSchema = {
  loginSchema: Yup.object().shape({
    mobile_number: Yup.string()
      .min(10, "Contact number must be at least 10 digits")
      .required("Please enter contact number"),
  }),
  step1: Yup.object().shape({
    business_name: Yup.string().required("Please enter business name"),
    assumed_name: Yup.string().required("Please enter assumed name"),
    location_name: Yup.string().required("Please enter location name"),
  }),
  step3: Yup.object().shape({
    // address_one: Yup.string().required("Please select location"),
    country: Yup.string().required("Please select country"),
    state: Yup.string().required("Please select state"),
    city: Yup.string().required("Please enter city name"),
    zipcode: Yup.string()
      .max(10, "Zip Code max 10 digits")
      .required("Please enter zip-code name"),
  }),
  step5: Yup.object().shape({
    description: Yup.string().required("Please Describe Your Business"),
  }),
  step7: Yup.object().shape({
    suggestion: Yup.string().required("Please share your comment/suggestion"),
  }),
};

export const empJrSchema = Yup.object().shape({
  monday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
  tuesday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
  wednesday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
  thursday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
  friday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
  saturday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
  sunday: Yup.array().of(
    Yup.object().shape({
      opining_time: Yup.string().required("Required"),
      closing_time: Yup.string().required("Required"),
    })
  ),
});
