import React from "react";
import { createBrowserRouter, Navigate } from "react-router-dom";
import { getStorage, getUserDetails } from "./helper/storage";
import { routerList, PublicRoute, ProtectRoutes } from "./router";
import { utils } from "./helper/utils";

const loggedIn = getStorage('logged-in')
const userDetails = getUserDetails()

const checkIsViewPermission = () => {
   return Object.keys(userDetails?.employee_role?.app_permissions).filter(item => userDetails?.employee_role?.app_permissions[item]?.view)
}

const permissions = userDetails?.employee_role?.app_permissions
  ? utils?.isObjectKeyEmpty(userDetails?.employee_role?.app_permissions)
    ? ["dashboard"]
    : ["dashboard", ...checkIsViewPermission()]
  : [];

  let list = routerList.filter((item) => {
    if (item?.key && loggedIn) {
      return permissions?.length > 0 ? permissions?.includes(item?.key) : true;
    } else {
      return true;
    }
  }).map((route) => ({
    ...route, element:
        route.private ? <ProtectRoutes><route.element /></ProtectRoutes> :
            <PublicRoute><route.element /></PublicRoute>
}))
export const routers = createBrowserRouter([
    {
        path: "/",
        element: (
            <Navigate to={loggedIn ? '/dashboard' : "/login"} />
        ),
    },
    ...list, // render public and private route
    {
        path: "*",
        element: (
            <Navigate to={loggedIn ? '/dashboard' : "/login"} />
        ),
    },
]);
export default routers;
