import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import CommonButton from "./CommonButton";
// import { Loader } from "../Loader";

const InputModal = (props) => {
  const {
    className,
    isOpen,
    toggle,
    onSubmit,
    headerName,
    submitLabel,
    inputProps,
    disabled = false,
    mxSize,
    fullscreen = false,
    btnClass = "",
    buttonFlex = false,
    cancelLabel = "Cancel",
    onCancel,
    loading = false,
    canDisabled = false,
    footer = true,
  } = props;

  return (
    <div>
      <Modal
        fullscreen={fullscreen}
        size={mxSize}
        isOpen={isOpen}
        toggle={toggle}
        className={className}
        backdrop="static"
        keyboard
        centered={true}
      >
        <ModalHeader toggle={toggle}>{headerName}</ModalHeader>
        <ModalBody>{inputProps}</ModalBody>
        <ModalFooter>
          <button
            disabled={canDisabled}
            onClick={onCancel}
            className="close_commonBtn"
          >
            {cancelLabel}
          </button>
          <CommonButton onClick={onSubmit} disabled={disabled || loading} name={submitLabel}>
            {/* {loading ? (
              <Loader color="dark" style={{ height: "18px", width: "18px" }} />
            ) : (
              submitLabel
            )} */}
          </CommonButton>
        </ModalFooter>
      </Modal>
    </div>
  );
};

export default InputModal;
