import * as Yup from "yup";

export const errorSchema = {
  loginSchema: Yup.object().shape({
    UserName: Yup.string().required("Required"),
    Password: Yup.string().required("Required"),
  }),
  customerSchema: Yup.object().shape({
    customerName: Yup.string().required("Please enter customer name"),
  }),
  step3: Yup.object().shape({
    // address_one: Yup.string().required("Please select location"),
    country: Yup.string().required("Please select country"),
    state: Yup.string().required("Please select state"),
    city: Yup.string().required("Please enter city name"),
    zipcode: Yup.string()
      .max(10, "Zip Code max 10 digits")
      .required("Please enter zip-code name"),
  }),
  step5: Yup.object().shape({
    description: Yup.string().required("Please Describe Your Business"),
  }),
  step7: Yup.object().shape({
    suggestion: Yup.string().required("Please share your comment/suggestion"),
  }),
};
