import config from "./_apiConfig/apiConfig";
import { performRequest, apiEndPoints, methodType } from "./_apiConfig";

export const userLogin = (data = {}) => {
  console.log("data",data)
  return performRequest(
    methodType.POST,
    config.baseURL + apiEndPoints.authLogin + '?' + data,
    {},
    false,
    false
  )
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
};

export const authSendOtp = (data = {}) => {
  return performRequest(
    methodType.POST,
    config.baseURL + apiEndPoints.mobileVerification,
    data,
    false,
    true
  )
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
};

export const authGoogleVerify = (data = {}) => {
  return performRequest(
    methodType.POST,
    config.baseURL + apiEndPoints.authGoogleRegister,
    data,
    false,
    true
  )
    .then((response) => {
      return response;
    })
    .catch((error) => {
      return error;
    });
};
