import React, { useEffect, useState } from 'react'
import CommonButton from '../../common/CommonButton'
import { getBillList } from '../../../services/billServies';

import { createCustomers, deleteCustomers, editCustomers, getCustomers } from '../../../services/customersService';
import InputModal from '../../common/InputModal';
import { InputField } from '../../common/InputField';
import { utils } from '../../../helper/utils';
import { errorSchema } from '../../../helper/errorSchema';
import * as XLSX from "xlsx";
import { toast } from 'react-toastify';
import { useNavigate } from 'react-router-dom';


export default function Bill() {

  const [billList, setBillList] = useState([])

  const [formValue, setFormValue] = useState({
    customerName: ""
  })
  const [isOpen, setIsOpen] = useState(false);
  const [formError, setFormError] = useState("");
  const [disableInp, setDisableInp] = useState({});
  const [loading, setLoading] = useState(false);
  const [isEdit, setIsEdit] = useState(false);


  const getBillData = async () => {
    const response = await getBillList();
    setBillList(response);
  }

  useEffect(() => {
    getBillData()
  }, [])


  const closeModal = () => {
    setIsOpen(false);
    setIsEdit(false);
    setFormValue({
      customerName: "",
    });
    setFormError("");
  };


  const handleOnChange = async (name, value) => {
    const stateObj = { ...formValue, [name]: value };
    setFormValue(stateObj);

    if (!!formError) {
      const error = await utils.checkFormError(stateObj, errorSchema.customerSchema);
      setFormError(error);
    }
  };

  const onSubmit = async () => {
    const validationResult = await utils.checkFormError(formValue, errorSchema.customerSchema);
    if (validationResult === true) {
      setLoading(true);
      if (isEdit) {
        const response = await editCustomers(formValue, formValue?.customerID);
        if (response?.responseData?.customerID) {
          toast.success('Data Updated Successfully');
          closeModal();
          getBillData();
        }
      } else {
        const response = await createCustomers(formValue);
        if (response?.responseData?.customerID) {
          toast.success('Customer Created Successfully');
          closeModal();
          getBillData();
        }
      }
    } else {
      setFormError(validationResult);
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    const response = await deleteCustomers(id);
    if (!response) {
      toast.success('Customer Deleted Successfully');
    }
    getBillData();
  };

  const handleEditAction = (item) => {
    setFormValue(item);
    setDisableInp(item);
    setIsOpen(true);
    setIsEdit(true);
    setFormError("");
  };

  const handleDownload = (e) => {
    e.preventDefault();

    const rows = billList?.map((product) => ({
      customerID: product.customerID,
      customerName: product?.customerName,
      primaryKeyID: product.primaryKeyID,
    }));

    const workbook = XLSX.utils.book_new();
    const worksheet = XLSX.utils.json_to_sheet(rows);

    XLSX.utils.book_append_sheet(workbook, worksheet, "customer");

    XLSX.utils.sheet_add_aoa(worksheet, [
      ["customerID", "customerName", "primaryKeyID"],
    ]);

    XLSX.writeFile(workbook, "msa.xlsx", { compression: true });
  };

  return (
    <div className='container'>
      <div className="common_flex p-3">
        <p className='bill_heading'> Bill </p>
        <div className='flex-gap'>
          <CommonButton name="Add" />
          <CommonButton name="Print" />
          <CommonButton name="Close" />
        </div>
      </div>

      <table class="table table-hover table-bordered">
        <thead>
          <tr>
            <th scope="col">Sr. No.</th>
            <th scope="col">Bill No</th>
            <th scope="col">Date</th>
            <th scope="col">Customer Name</th>
            <th scope="col">Net Amount</th>
            <th scope="col">Remarks</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        {
          billList?.length > 0 ? (
            billList?.map((el, ind) => {
              return (
                <tbody>
                  <tr>
                    <th scope="row">{ind + 1}</th>
                    <td>{el?.billNo}</td>
                    <td>{new Date(el?.billDate).toDateString()}</td>
                    <td>{el?.customerName}</td>
                    <td>{(el?.netAmount).toFixed(2)}</td>
                    <td>{el?.remarks ? el?.remarks : '-'}</td>
                    <td>
                      <span className='questions_create'> <i class='bx bx-edit-alt'></i> </span> &nbsp;
                      <span className='questions_delete'> <i class='bx bxs-trash'></i> </span>
                    </td>
                  </tr>
                </tbody>
              )
            })
          ) : (
            <> No data found </>
          )
        }
      </table>

      <InputModal
        isOpen={Boolean(isOpen)}
        headerName={isEdit ? "Edit Customer" : "Add Customer"}
        toggle={() => closeModal()}
        submitLabel={isEdit ? "Update Customer" : "Add Customer"}
        onSubmit={(e) => onSubmit(e)}
        disabled={isEdit ? JSON.stringify(formValue) === JSON.stringify(disableInp) : ""}
        loading={loading}
        inputProps={
          <>
            <div className="row">
              <div className="col-md-12">
                <label className="modal_label mb-2"> {<span> Employee <span className="redStar_color"> * </span></span>}</label>
                <InputField
                  label="User Name *"
                  placeholder="User Name"
                  name='UserName'
                  type='text'
                  value={formValue?.UserName}
                  focus={!!(typeof formError === "object" && formError?.UserName)}
                  error={!!(typeof formError === "object") ? formError?.UserName : ""}
                  onChange={({ target: { name, value } }) =>
                    handleOnChange(name, value)
                  }
                />
              </div>
            </div>
          </>
        }
      />
    </div>
  )
}
