import React from "react";

const TextArea = (props) => {
  const {
    name,
    error,
    label,
    focus,
    error_class = "",
    onChange,
    value,
    disabled = false,
    max = 1000,
    placeholder = "",
    className = "",
  } = props;
  const invalid = !!(focus && error);
  const valid = !!Boolean(focus && !error);

  return (
    <>
      <div>
        <div>
          <textarea
            disabled={disabled}
            autoComplete="off"
            name={name}
            id={name}
            className={className}
            placeholder={placeholder ? placeholder : label}
            onChange={onChange}
            value={value}
            maxLength={max}
          />
        </div>
        {error && (
          <p className=" !text-red-600 text-xs mt-[2px]">
            {error}
          </p>
        )}
      </div>
    </>
  );
};

export default TextArea;
