import React, { useEffect, useState } from 'react'
import CommonButton from '../../common/CommonButton'
import { getCustomers } from '../../../services/customersService';
import InputModal from '../../common/InputModal';
import { InputField } from '../../common/InputField';
import { utils } from '../../../helper/utils';

export default function Customer() {

  const [customerList, setCustomersList] = useState([])

  const [isOpen, setIsOpen] = useState(false);
  const [formError, setFormError] = useState("");
  const [disableInp, setDisableInp] = useState({});
  const [loading, setLoading] = useState(false);

  const [openDel, setOpenDel] = useState(false);
  const [isEdit, setIsEdit] = useState(false);

  const getCustomerList = async () => {
    const response = await getCustomers();
    setCustomersList(response);
  }

  useEffect(() => {
    getCustomerList()
  }, [])

  const closeModal = () => {
    setIsOpen(false);
    setIsEdit(false);
    setFormValue({
      emp_id: "",
    });
    setFormError("");
  };


  const handleOnChange = async (name, value) => {
    const stateObj = { ...formValue, [name]: value };
    setFormValue(stateObj);

    if (!!formError) {
      const error = await utils.checkFormError(stateObj, attendanceSchema);
      setFormError(error);
    }
  };

  const onSubmit = async () => {
    const validationResult = await checkFormError(formValue, attendanceSchema);
    if (validationResult === true) {
      setLoading(true);
      if (isEdit === true) {
        updateMutation.mutate(formValue);
      } else {
        addMutation.mutate(formValue);
      }
    } else {
      setFormError(validationResult);
      setLoading(false);
    }
  };

  const handleDelete = () => {
    setLoading(true);
    deleteMutation.mutate(openDel);
  };
  const handleEditAction = (item) => {
    setFormValue(item);
    setDisableInp(item);
    setIsOpen(true);
    setIsEdit(true);
    setFormError("");
  };

  return (
    <div className='container'>
      <div className="common_flex p-3">
        <p className='bill_heading'> Customer </p>
        <div className='flex-gap'>
          <CommonButton name="Add" onClick={() => setIsOpen(!isOpen)} />
          <CommonButton name="Print" />
          <CommonButton name="Close" />
        </div>
      </div>

      <table class="table table-hover table-bordered">
        <thead>
          <tr>
            <th scope="col">Sr. No.</th>
            <th scope="col">Customer Name</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        {
          customerList?.length > 0 ? (
            customerList?.map((el, ind) => {
              return (
                <React.Fragment key={ind + '123'}>
                  <tbody>
                    <tr>
                      <th scope="row">{ind + 1}</th>
                      <td> {el?.customerName} </td>
                      <td>
                        <span className='questions_create'> <i class='bx bx-edit-alt'></i> </span> &nbsp;
                        <span className='questions_delete'> <i class='bx bxs-trash'></i> </span>
                      </td>
                    </tr>
                  </tbody>
                </React.Fragment>
              )
            })
          ) : (
            <> No data found </>
          )
        }
      </table>

      <InputModal
        isOpen={Boolean(isOpen)}
        headerName={isEdit ? "Edit Customer" : "Add Customer"}
        toggle={() => closeModal()}
        submitLabel={isEdit ? "Update Customer" : "Add Customer"}
        onSubmit={(e) => onSubmit(e)}
        disabled={isEdit ? JSON.stringify(formValue) === JSON.stringify(disableInp) : ""}
        loading={loading}
        inputProps={
          <>
            <div className="row">
              <div className="col-md-12">
                <label className="modal_label mb-2"> {<span> Employee <span className="redStar_color"> * </span></span>}</label>
                <InputField
                  label="Customer Name *"
                  placeholder="Customer Name"
                  name='customerName'
                  type='text'
                  value={formValue?.customerName}
                  focus={!!(typeof formError === "object" && formError?.customerName)}
                  error={!!(typeof formError === "object") ? formError?.customerName : ""}
                  onChange={({ target: { name, value } }) =>
                    handleOnChange(name, value)
                  }
                />
              </div>
            </div>
          </>
        }
      />
    </div>
  )
}
