import React, { useState } from "react";
// import { Loader } from "../Loader";
import { Modal } from "bootstrap";

const InputModal = (props) => {
  const {
    className,
    isOpen,
    toggle,
    onSubmit,
    headerName,
    submitLabel,
    inputProps,
    disabled = false,
    mxSize,
    fullscreen = false,
    btnClass = "",
    buttonFlex = false,
    cancelLabel = "Cancel",
    onCancel,
    loading = false,
    canDisabled = false,
    footer = true,
  } = props;

  return (
    <>
      <Modal
        fullscreen={fullscreen}
        size={mxSize}
        isOpen={isOpen}
        toggle={toggle}
        className={className}
        backdrop="static"
        keyboard
        centered={true}
      >
        <div className="modal-header">
          {headerName}
        </div>
        <div className="modal-body">{inputProps}</div>
        <div className="modal-footer">
          <button
            disabled={canDisabled}
            onClick={onCancel}
            className="btn btn-outline-secondary"
          >
            {cancelLabel}
          </button>
          <button onClick={onSubmit} disabled={disabled || loading}>
            {submitLabel}
            {/* {loading ? (
              <Loader color="dark" style={{ height: "18px", width: "18px" }} />
            ) : (
              submitLabel
            )} */}
          </button>
        </div>
      </Modal>
    </>
  );
};

export default InputModal;
