import cookie from "js-cookie";
const userKey = "redcredentials";

export const setStorage = (key, data) => {
  cookie.set(key, JSON.stringify(data));
};

export const getStorage = (key) => {
  let session = cookie.get(key);
  return session ? JSON.parse(session) : null;
};

export const removeStorage = (key) => {
  let value = cookie.remove(key);
  return Boolean(value) && typeof value === "string" ? JSON.parse(value) : null;
};

export const clearStorage = (callBack) => {
  const cookies = cookie.get();
  for (const cookieName in cookies) {
    if (cookies.hasOwnProperty(cookieName)) {
      cookie.remove(cookieName);
    }
  }
  if (Boolean(callBack)) {
    callBack();
  }
};

export const getUserDetails = () => {
  let session = getStorage(userKey);
  return session
    ? {
      data: session,
      isAuthenticate: Boolean(session),
    }
    : { data: {}, isAuthenticate: false };
};

export const getUserToken = () => {
  let session = getUserDetails();
  return session?.isAuthenticate ? session?.data?.access_token : null;
};

export const signIn = (data) => {
  setStorage(userKey, data); // key = rrrrcredentials
};

export const signOut = (callBack) => {
  removeStorage(userKey);
  callBack && callBack();
};
